using System;\nusing System.IO;\nusing System.Web;\n\nnamespace YourNamespace\n{\n    public class PdfHandler : IHttpHandler\n    {\n        public void ProcessRequest(HttpContext context)\n        {\n            // Get the PDF data from your source (e.g., database, file, etc.)\n            Byte[] pdfBytes = File.ReadAllBytes("c:\\temp\\Test.pdf");\n\n            // Set the response headers\n            context.Response.ContentType = "application/pdf";\n            context.Response.AddHeader("Content-Disposition", "inline; filename=TestDoc.pdf");\n            context.Response.AddHeader("Content-Length", pdfBytes.Length.ToString());\n\n            // Write the PDF data to the response\n            context.Response.OutputStream.Write(pdfBytes, 0, pdfBytes.Length);\n        }\n\n        public bool IsReusable\n        {\n            get { return false; }\n        }\n    }\n}