using System;\nusing System.Xml;\n\nnamespace SharePointWebService\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            // Create a new instance of the Lists web service\n            Lists lists = new Lists();\n\n            // Set the URL of the SharePoint site\n            lists.Url = "http://your-sharepoint-site/_vti_bin/lists.asmx";\n\n            // Authenticate the web service\n            lists.Credentials = new System.Net.NetworkCredential("username", "password", "domain");\n\n            // Define the list ID and the new flags value\n            string listId = "your-list-id";\n            int flags = 1; // Enable management of content types\n\n            // Create an XML element for the list properties\n            XmlDocument xmlDoc = new XmlDocument();\n            XmlElement listProperties = xmlDoc.CreateElement("ListProperties");\n\n            // Add the Flags attribute to the list properties\n            XmlAttribute flagsAttr = xmlDoc.CreateAttribute("Flags");\n            flagsAttr.Value = flags.ToString();\n            listProperties.Attributes.Append(flagsAttr);\n\n            // Update the list using the web service\n            lists.UpdateList(listId, listProperties);\n        }\n    }\n}