using System;\nusing System.IO;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class FileComparer\n{\n    public static bool CompareFiles(string file1, string file2)\n    {\n        using (var hashAlgorithm = SHA256.Create())\n        {\n            var file1Hash = GetFileHash(file1, hashAlgorithm);\n            var file2Hash = GetFileHash(file2, hashAlgorithm);\n\n            return file1Hash.SequenceEqual(file2Hash);\n        }\n    }\n\n    private static byte[] GetFileHash(string filePath, HashAlgorithm hashAlgorithm)\n    {\n        using (var fileStream = File.OpenRead(filePath))\n        {\n            return hashAlgorithm.ComputeHash(fileStream);\n        }\n    }\n}