using System;\nusing System.Configuration;\nusing System.Xml.Xsl;\n\nnamespace UserTransform\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            string oldXML = ConfigurationManager.AppSettings["XMLToBeTransformed"];\n            string xsltLocation = ConfigurationManager.AppSettings["XSLTfile"];\n            string newCSV = ConfigurationManager.AppSettings["NewCSVLocation"];\n\n            try\n            {\n                XslCompiledTransform transform = new XslCompiledTransform();\n                transform.Load(xsltLocation);\n                transform.Transform(oldXML, newCSV);\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine("Error: " + ex.Message);\n            }\n        }\n    }\n}