using System;\nusing System.Net;\nusing System.Net.Sockets;\nusing System.Text;\nusing System.Threading;\n\nclass TcpServer\n{\n    private TcpListener _listener;\n\n    public TcpServer(int port)\n    {\n        _listener = new TcpListener(IPAddress.Any, port);\n    }\n\n    public void Start()\n    {\n        _listener.Start();\n        Console.WriteLine("Server started. Listening on port " + _listener.LocalEndpoint);\n\n        while (true)\n        {\n            TcpClient client = _listener.AcceptTcpClient();\n            Console.WriteLine("Client connected: " + client.Client.RemoteEndPoint);\n\n            // Handle client communication\n            Thread thread = new Thread(() => HandleClient(client));\n            thread.Start();\n        }\n    }\n\n    private void HandleClient(TcpClient client)\n    {\n        // Send messages to the client\n        NetworkStream stream = client.GetStream();\n        byte[] message = Encoding.ASCII.GetBytes("Hello, client!");\n        stream.Write(message, 0, message.Length);\n\n        // Handle client responses\n        byte[] buffer = new byte[1024];\n        int bytesRead = stream.Read(buffer, 0, buffer.Length);\n        Console.WriteLine("Received from client: " + Encoding.ASCII.GetString(buffer, 0, bytesRead));\n\n        // Close the connection\n        client.Close();\n    }\n}