using System;\nusing System.Net.NetworkInformation;\n\npublic class PacketLoss\n{\n    public static void GetPacketLoss()\n    {\n        var ping = new Ping();\n        var reply = ping.Send("www.stackoverflow.com");\n\n        if (reply.Status == IPStatus.Success)\n        {\n            Console.WriteLine("Packet loss: " + reply.RoundtripTime + "ms");\n        }\n        else\n        {\n            Console.WriteLine("Packet loss: " + reply.Status);\n        }\n    }\n\n    public static void GetTraceRoute()\n    {\n        var ping = new Ping();\n        var options = new PingOptions(1, true);\n\n        for (int i = 1; i <= 30; i++)\n        {\n            var reply = ping.Send("www.stackoverflow.com", 1000, new byte[0], options);\n\n            if (reply.Status == IPStatus.Success)\n            {\n                Console.WriteLine("Hop " + i + ": " + reply.Address);\n            }\n            else\n            {\n                Console.WriteLine("Hop " + i + ": " + reply.Status);\n            }\n\n            options.Ttl += 1;\n        }\n    }\n}