using System;\nusing System.Diagnostics;\nusing System.Timers;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        try\n        {\n            // Declare new process and name it p1\n            Process p1 = Process.Start("iexplore", "http://www.google.com");\n\n            // Create a timer\n            Timer timer = new Timer(120000); // 120000 milliseconds = 2 minutes\n            timer.Elapsed += (sender, e) => KillProcess(p1);\n            timer.Start();\n\n            Console.ReadLine();\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Problem with Process: {0}", ex.Message);\n        }\n    }\n\n    static void KillProcess(Process process)\n    {\n        try\n        {\n            process.Kill();\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error killing process: {0}", ex.Message);\n        }\n    }\n}