using System;\nusing System.Collections.Generic;\n\npublic class Program\n{\n    public static int NetworkDays(DateTime startDate, DateTime endDate, List<DateTime> holidays = null)\n    {\n        int workDays = 0;\n        DateTime currentDay = startDate;\n\n        while (currentDay <= endDate)\n        {\n            // Check if the current day is a weekday\n            if (currentDay.DayOfWeek != DayOfWeek.Saturday && currentDay.DayOfWeek != DayOfWeek.Sunday)\n            {\n                // Check if the current day is not a holiday\n                if (holidays == null || !holidays.Contains(currentDay))\n                {\n                    workDays++;\n                }\n            }\n\n            currentDay = currentDay.AddDays(1);\n        }\n\n        return workDays;\n    }\n\n    public static void Main()\n    {\n        DateTime startDate = new DateTime(2022, 1, 1);\n        DateTime endDate = new DateTime(2022, 1, 31);\n\n        List<DateTime> holidays = new List<DateTime> { new DateTime(2022, 1, 1), new DateTime(2022, 1, 15) };\n\n        int workDays = NetworkDays(startDate, endDate, holidays);\n\n        Console.WriteLine("Number of workdays: " + workDays);\n    }\n}