using System.Data.SqlClient;\n\npublic class DataAccess\n{\n    public void ExecuteQuery(string connectionString, string query, SqlParameter[] parameters)\n    {\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            connection.Open();\n\n            SqlCommand command = new SqlCommand(query, connection);\n            command.Parameters.AddRange(parameters);\n\n            command.ExecuteNonQuery();\n        }\n    }\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        string connectionString = "Your connection string";\n        string query = "INSERT INTO Users (Username, Password) VALUES (@Username, @Password)";\n\n        SqlParameter[] parameters = new SqlParameter[]\n        {\n            new SqlParameter("@Username", "JohnDoe"),\n            new SqlParameter("@Password", "SecurePassword")\n        };\n\n        DataAccess dataAccess = new DataAccess();\n        dataAccess.ExecuteQuery(connectionString, query, parameters);\n    }\n}