using System;\nusing System.IO;\nusing System.Net;\n\npublic static bool IsLocalPath(string path)\n{\n    Uri uri = new Uri(path);\n    if (!uri.IsUnc)\n    {\n        // If it's not UNC, it's a local drive letter path\n        return true;\n    }\n\n    string machineName = uri.Host;\n    string localMachineName = Dns.GetHostName();\n    IPAddress[] localIPs = Dns.GetHostAddresses(localMachineName);\n\n    // Check if the machine name matches the local machine name\n    if (machineName.Equals(localMachineName, StringComparison.OrdinalIgnoreCase))\n    {\n        return true;\n    }\n\n    // Check if the IP address matches any of the local IP addresses\n    foreach (IPAddress localIP in localIPs)\n    {\n        if (machineName.Equals(localIP.ToString()))\n        {\n            return true;\n        }\n    }\n\n    return false;\n}