using System.IO;\nusing System.Text.RegularExpressions;\n\n// Define the delimiter and quote character\nchar delimiter = 'þ';\nchar quote = 'þ';\n\n// Create a TextReader instance\nusing (var reader = new StreamReader("your_file.txt"))\n{\n    string line;\n    while ((line = reader.ReadLine()) != null)\n    {\n        // Split the line into fields using the delimiter\n        var fields = line.Split(new[] { delimiter }, StringSplitOptions.None);\n\n        // Process the fields\n        foreach (var field in fields)\n        {\n            Console.WriteLine(field.Trim(quote)); // Remove quotes\n        }\n    }\n}