// Assuming you're using ASP.NET and have access to the current HttpContext\nvar cookies = HttpContext.Current.Request.Cookies;\n\nWebClient myWebClient = new WebClient();\nmyWebClient.Headers.Add(HttpRequestHeader.Cookie, string.Join(";", cookies.Cast<Cookie>().Select(c => $"{c.Name}={c.Value}")));\n\nstring str = myWebClient.DownloadString("http://localhost/myxml.aspx");\nResponse.Write(str.ToString());