// WCF Service Hosting example (using IIS)\nusing System;\nusing System.ServiceModel;\n\nnamespace MyWCFService\n{\n    [ServiceContract]\n    public interface IMyService\n    {\n        [OperationContract]\n        string MyMethod();\n    }\n\n    public class MyService : IMyService\n    {\n        public string MyMethod()\n        {\n            return "Hello, World!";\n        }\n    }\n}