public class DynamicSiteMapProvider : StaticSiteMapProvider\n{\n    public override SiteMapNode GetNodeFromUrl(string url)\n    {\n        // Define a pattern to match URLs under "articles"\n        string pattern = "/articles/*";\n\n        // Check if the URL matches the pattern\n        if (Regex.IsMatch(url, pattern))\n        {\n            // Create a new node with the matched URL\n            SiteMapNode node = new SiteMapNode(this.Provider, url);\n            node.Url = url;\n            node.Title = "Article";\n            return node;\n        }\n\n        // If no match, call the base method\n        return base.GetNodeFromUrl(url);\n    }\n}