XElement currentReport = (from item in callReports.Descendants("callReport")\n                          where (int)item.Element("localId") == myCallreports.LocalId\n                          select item).FirstOrDefault();\n\n// ...\n\n// Parse the HTML as an XML fragment\nXElement recommendations = XElement.Parse(myCallreports.Comments);\nXElement reactions = XElement.Parse(myCallreports.Ownerreaction);\n\n// Add the parsed HTML to the XML document\ncurrentReport.Element("recomendations").ReplaceNodes(recommendations);\ncurrentReport.Element("reactions").ReplaceNodes(reactions);