// SharedConstants.cs (shared project)\npublic static class ResourceKeys\n{\n    public const string ValidationError = "ValidationError";\n    // ...\n}\n\n// ModelResources.resx (Model project)\ninternal class ModelResources\n{\n    public static string ValidationError => ResourceManager.GetString(ResourceKeys.ValidationError, CultureInfo.CurrentCulture);\n    // ...\n}\n\n// ViewResources.resx (View project)\ninternal class ViewResources\n{\n    public static string ValidationError => ResourceManager.GetString(ResourceKeys.ValidationError, CultureInfo.CurrentCulture);\n    // ...\n}\n\n// ResourceHelper.cs (shared project)\npublic static class ResourceHelper\n{\n    public static string GetString(string key)\n    {\n        // Load the correct resource file based on the current culture\n        var culture = CultureInfo.CurrentCulture;\n        var resourceManager = new ResourceManager(typeof(ModelResources)); // or ViewResources\n        return resourceManager.GetString(key, culture);\n    }\n}