using System;\nusing System.Net;\nusing System.Xml;\n\npublic class VentriloStatus\n{\n    private string _ventriloServerIp = "your_ventrilo_server_ip";\n    private int _ventriloServerPort = 3784; // default port\n    private string _apiKey = "your_api_key";\n\n    public void GetStatus()\n    {\n        // Create a WebRequest to connect to the Ventrilo Server API\n        HttpWebRequest request = (HttpWebRequest)WebRequest.Create($"http://{_ventriloServerIp}:{_ventriloServerPort}/status?api_key={_apiKey}");\n\n        // Send the request and get the response\n        HttpWebResponse response = (HttpWebResponse)request.GetResponse();\n\n        // Read the response (XML format)\n        string xmlString = new StreamReader(response.GetResponseStream()).ReadToEnd();\n\n        // Parse the response\n        XmlDocument xmlDoc = new XmlDocument();\n        xmlDoc.LoadXml(xmlString);\n\n        // Extract the user and channel information\n        XmlNodeList users = xmlDoc.SelectNodes("//user");\n        foreach (XmlNode user in users)\n        {\n            string name = user.SelectSingleNode("name").InnerText;\n            string channel = user.SelectSingleNode("channel").InnerText;\n\n            Console.WriteLine($"{name} is connected to {channel}");\n        }\n    }\n}