using System;\nusing System.Runtime.InteropServices;\n\n// Create a manifest file for the COM component\n// This can be done using the regsvr32 tool or by manually creating a manifest file\n// For example:\n//   regsvr32 /i MyComComponent.dll\n\n// Embed the manifest file in the ASP.Net application\n// For example:\n//   [assembly: System.Web.UI.WebResource("MyComComponent.manifest", "application/x-ms-manifest")]\n\npublic partial class MyPage : System.Web.UI.Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        // Create an instance of the COM component\n        MyComComponent component = new MyComComponent();\n\n        // Use the COM component\n        component.DoSomething();\n    }\n}\n\n// Define the COM component\n[ComVisible(true)]\npublic class MyComComponent\n{\n    [ComVisible(true)]\n    public void DoSomething()\n    {\n        // Do something\n    }\n}