using System.Management;\n\nclass AntivirusDefinitions\n{\n    public static string GetSymantecDefinitionsDate(string computerName)\n    {\n        string definitionsDate = string.Empty;\n\n        try\n        {\n            ManagementScope scope = new ManagementScope(@"\\" + computerName + @"\root\wmi");\n            ObjectQuery query = new ObjectQuery("SELECT * FROM SymantecEndpointProtection");\n\n            ManagementObjectSearcher searcher = new ManagementObjectSearcher(scope, query);\n\n            foreach (ManagementObject item in searcher.Get())\n            {\n                definitionsDate = item["PatternFileDate"].ToString();\n                break;\n            }\n        }\n        catch (ManagementException e)\n        {\n            Console.WriteLine("An error occurred: " + e.Message);\n        }\n\n        return definitionsDate;\n    }\n}