using System;\nusing System.IO;\nusing System.Security.Cryptography;\n\npublic class PemReader\n{\n    public static RSACryptoServiceProvider ReadPrivateKey(string pemPath)\n    {\n        using (var reader = new StreamReader(pemPath))\n        {\n            string pemContent = reader.ReadToEnd();\n            pemContent = pemContent.Replace("-----BEGIN RSA PRIVATE KEY-----", "");\n            pemContent = pemContent.Replace("-----END RSA PRIVATE KEY-----", "");\n            byte[] privateKeyBytes = Convert.FromBase64String(pemContent);\n            var rsa = RSA.Create();\n            rsa.ImportPkcs8PrivateKey(privateKeyBytes, out _);\n            return (RSACryptoServiceProvider)rsa;\n        }\n    }\n}