using System;\nusing System.DirectoryServices;\n\nnamespace IISHelper\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            string websiteName = "MyDevelopmentWebsite";\n            string physicalPath = @"C:\inetpub\wwwroot\mywebsite";\n\n            DirectoryEntry iis = new DirectoryEntry("IIS://localhost/W3SVC");\n            DirectoryEntry website = iis.Children.Add(websiteName, "IIsWebServer");\n\n            website.Properties["ServerBindings"].Value = "localhost:80";\n            website.Properties["Root"].Value = physicalPath;\n            website.CommitChanges();\n        }\n    }\n}