// Define the interface for your user control\npublic interface ISpiffyControl\n{\n    string CustomProperty1 { get; set; }\n    int CustomProperty2 { get; set; }\n}\n\n// Make sure your user control implements the interface\npublic partial class SpiffyControl : UserControl, ISpiffyControl\n{\n    public string CustomProperty1 { get; set; }\n    public int CustomProperty2 { get; set; }\n}\n\n// In your base page class\npublic class SpiffyPage : Page\n{\n    protected override void OnLoad(EventArgs e)\n    {\n        base.OnLoad(e);\n\n        // Load the user control\n        Control control = LoadControl("~/Controls/SpiffyControl.ascx");\n\n        // Cast the control to the interface type\n        ISpiffyControl spiffyControl = control as ISpiffyControl;\n\n        // Now you can access the custom properties\n        spiffyControl.CustomProperty1 = "Hello";\n        spiffyControl.CustomProperty2 = 42;\n\n        // Add the control to the page\n        Page.Controls.Add(control);\n    }\n}