using System.Security.Cryptography;\nusing System.Text;\n\n// Generate a test RSA key pair\nRSACryptoServiceProvider rsa = new RSACryptoServiceProvider(2048);\nstring publicKey = rsa.ToXmlString(false);\nstring privateKey = rsa.ToXmlString(true);\n\n// Encrypt a test message using the public key\nbyte[] message = Encoding.UTF8.GetBytes("Test message");\nbyte[] encryptedMessage = rsa.Encrypt(message, false);\n\n// Decrypt the message using the private key\nbyte[] decryptedMessage = rsa.Decrypt(encryptedMessage, false);\nstring decryptedText = Encoding.UTF8.GetString(decryptedMessage);