using System.IO.Pipes;\nusing System.Text;\n\npublic class SharedResources\n{\n    private static readonly string _pipeName = "mysharedresources";\n\n    public static string GetData()\n    {\n        using (var pipeClient = new NamedPipeClientStream(".", _pipeName, PipeDirection.In))\n        {\n            pipeClient.Connect();\n            using (var reader = new StreamReader(pipeClient))\n            {\n                return reader.ReadToEnd();\n            }\n        }\n    }\n\n    public static void SetData(string data)\n    {\n        using (var pipeServer = new NamedPipeServerStream(_pipeName, PipeDirection.Out))\n        {\n            pipeServer.WaitForConnection();\n            using (var writer = new StreamWriter(pipeServer))\n            {\n                writer.Write(data);\n            }\n        }\n    }\n}