using System;\nusing System.IO.MemoryMappedFiles;\n\nclass SharedMemoryExample\n{\n    public static void CreateSharedMemoryBlock(string name, byte[] data)\n    {\n        using (var mmf = MemoryMappedFile.CreateNew(name, data.Length))\n        {\n            using (var stream = mmf.CreateViewStream())\n            {\n                stream.Write(data, 0, data.Length);\n            }\n        }\n    }\n}