using System;\nusing System.IO;\nusing System.Security.Principal;\n\nclass WebsiteBlocker\n{\n    private const string HOSTS_FILE = @"C:\Windows\System32\drivers\etc\hosts";\n    private const string BLOCKED_IP = "127.0.0.1";\n\n    public void BlockWebsite(string domainName)\n    {\n        if (!IsAdmin())\n        {\n            throw new InvalidOperationException("You must run this program as an administrator.");\n        }\n\n        string hostsContent = File.ReadAllText(HOSTS_FILE);\n        if (!hostsContent.Contains(domainName))\n        {\n            using (StreamWriter writer = File.AppendText(HOSTS_FILE))\n            {\n                writer.WriteLine($"{BLOCKED_IP} {domainName}");\n            }\n        }\n    }\n\n    public void UnblockWebsite(string domainName)\n    {\n        if (!IsAdmin())\n        {\n            throw new InvalidOperationException("You must run this program as an administrator.");\n        }\n\n        string hostsContent = File.ReadAllText(HOSTS_FILE);\n        string newContent = hostsContent.Replace($"{BLOCKED_IP} {domainName}", string.Empty);\n        File.WriteAllText(HOSTS_FILE, newContent);\n    }\n\n    private bool IsAdmin()\n    {\n        WindowsIdentity identity = WindowsIdentity.GetCurrent();\n        WindowsPrincipal principal = new WindowsPrincipal(identity);\n        return principal.IsInRole(WindowsBuiltInRole.Administrator);\n    }\n}