using System;\nusing System.Data.SqlClient;\n\npublic class QueryRouter\n{\n    private readonly string[] _nodeConnections;\n\n    public QueryRouter(string[] nodeConnections)\n    {\n        _nodeConnections = nodeConnections;\n    }\n\n    public SqlConnection GetLeastBusyConnection()\n    {\n        // Implement logic to determine the least busy node\n        // For simplicity, this example just returns a random connection\n        var random = new Random();\n        return new SqlConnection(_nodeConnections[random.Next(_nodeConnections.Length)]);\n    }\n}