using System.Xml;\n\n// Load the XML file\nstring xmlUrl = "http://sites.google.com/site/shadchanproject/Home/lots1.xml";\nXmlDocument xmlDoc = new XmlDocument();\nxmlDoc.Load(xmlUrl);\n\n// Select the nodes you want to display\nXmlNodeList nodes = xmlDoc.SelectNodes("//a7190 | //a7193"); // Add the third node name if needed\n\n// Create a DataTable to store the data\nDataTable dt = new DataTable();\ndt.Columns.Add("Value");\ndt.Columns.Add("Text");\n\n// Populate the DataTable\nforeach (XmlNode node in nodes)\n{\n    DataRow dr = dt.NewRow();\n    dr["Value"] = node.InnerText;\n    dr["Text"] = node.InnerText;\n    dt.Rows.Add(dr);\n}\n\n// Bind the DataTable to the DropDownList\nDropDownList1.DataSource = dt;\nDropDownList1.DataValueField = "Value";\nDropDownList1.DataTextField = "Text";\nDropDownList1.DataBind();