using System;\nusing System.IO;\nusing System.Linq;\n\nclass Quiz\n{\n    static void Main(string[] args)\n    {\n        // Read quiz data from file\n        string[] lines = File.ReadAllLines("quiz.txt");\n\n        // Loop through each question\n        for (int i = 0; i < lines.Length; i += 6)\n        {\n            // Read question and answers\n            string question = lines[i];\n            string[] answers = lines.Skip(i + 1).Take(4).ToArray();\n            string correctAnswer = lines[i + 5];\n\n            // Ask user for answer\n            Console.WriteLine(question);\n            for (int j = 0; j < answers.Length; j++)\n            {\n                Console.WriteLine($"{j + 1}. {answers[j]}");\n            }\n            Console.Write("Enter your answer (1-4): ");\n            int userAnswer = Convert.ToInt32(Console.ReadLine()) - 1;\n\n            // Check if user's answer is correct\n            if (answers[userAnswer] == correctAnswer)\n            {\n                Console.WriteLine("Correct!");\n            }\n            else\n            {\n                Console.WriteLine($"Sorry, the correct answer was {correctAnswer}");\n            }\n        }\n    }\n}