using (var connectionA = new SqlConnection(connectionStringA))\nusing (var connectionB = new SqlConnection(connectionStringB))\n{\n    connectionA.Open();\n    connectionB.Open();\n\n    using (var sqlCmdA = new SqlCommand(sqlStatementA, connectionA))\n    using (var sqlCmdB = new SqlCommand(sqlStatementB, connectionB))\n    {\n        sqlCmdA.ExecuteNonQuery();\n        sqlCmdB.ExecuteNonQuery();\n    }\n}