protected void Application_Start()\n{\n    AreaRegistration.RegisterAllAreas();\n    RegisterRoutes(RouteTable.Routes);\n    ControllerBuilder.Current.SetControllerFactory(new HttpOverrideControllerFactory());\n}\n\npublic class HttpOverrideControllerFactory : DefaultControllerFactory\n{\n    protected override Type GetControllerType(RequestContext requestContext, string controllerName)\n    {\n        var controllerType = base.GetControllerType(requestContext, controllerName);\n        if (controllerType != null)\n        {\n            var overrideAttribute = Attribute.GetCustomAttribute(controllerType, typeof(HttpOverrideAttribute)) as HttpOverrideAttribute;\n            if (overrideAttribute != null)\n            {\n                return controllerType;\n            }\n        }\n        return null;\n    }\n}