using System;\nusing System.Web.Mvc;\n\n[AttributeUsage(AttributeTargets.Method, AllowMultiple = false, Inherited = false)]\npublic class HttpOverrideAttribute : ActionMethodSelectorAttribute\n{\n    public override bool IsValidForRequest(ControllerContext controllerContext, MethodInfo methodInfo)\n    {\n        var request = controllerContext.HttpContext.Request;\n        var overrideMethod = request.Headers["X-HTTP-Method-Override"];\n        if (!string.IsNullOrEmpty(overrideMethod))\n        {\n            var actualMethod = request.HttpMethod;\n            if (actualMethod == "GET" && overrideMethod == "DELETE")\n            {\n                return true;\n            }\n        }\n        return base.IsValidForRequest(controllerContext, methodInfo);\n    }\n}