using System;\nusing System.Windows.Forms;\nusing System.Runtime.InteropServices;\n\nnamespace SilverlightHost\n{\n    [ComImport]\n    [Guid("D3E34B21-9D75-101A-8C3D-00AA0018295F")]\n    public interface ISilverlightCtrl\n    {\n        void CreateControl();\n    }\n\n    public partial class SilverlightHostForm : Form\n    {\n        private ISilverlightCtrl silverlightCtrl;\n\n        public SilverlightHostForm()\n        {\n            InitializeComponent();\n\n            // Create the Silverlight ActiveX control\n            silverlightCtrl = (ISilverlightCtrl)new SilverlightCtrl();\n            silverlightCtrl.CreateControl();\n\n            // Set the control's size and position\n            silverlightCtrl.GetType().InvokeMember("Width", BindingFlags.SetProperty, null, silverlightCtrl, new object[] { 800 });\n            silverlightCtrl.GetType().InvokeMember("Height", BindingFlags.SetProperty, null, silverlightCtrl, new object[] { 600 });\n            silverlightCtrl.GetType().InvokeMember("Left", BindingFlags.SetProperty, null, silverlightCtrl, new object[] { 0 });\n            silverlightCtrl.GetType().InvokeMember("Top", BindingFlags.SetProperty, null, silverlightCtrl, new object[] { 0 });\n\n            // Add the control to the form\n            this.Controls.Add((Control)silverlightCtrl);\n        }\n    }\n}