using System;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic static byte[] HashGuids(Guid[] guids)\n{\n    using (var sha256 = SHA256.Create())\n    {\n        var byteBuffer = new byte[guids.Length * 16];\n        for (int i = 0; i < guids.Length; i++)\n        {\n            var guidBytes = guids[i].ToByteArray();\n            Array.Copy(guidBytes, 0, byteBuffer, i * 16, 16);\n        }\n        return sha256.ComputeHash(byteBuffer);\n    }\n}