using System;\nusing System.Data.SqlClient;\n\n// Retrieve the record from database\nSqlConnection connection = new SqlConnection("Your Connection String");\nSqlCommand command = new SqlCommand("SELECT RecordTimestamp FROM MyTable", connection);\nconnection.Open();\nSqlDataReader reader = command.ExecuteReader();\n\nwhile (reader.Read())\n{\n    DateTime utcTimestamp = reader.GetDateTime(0);\n    TimeZoneInfo localTimeZone = TimeZoneInfo.FindSystemTimeZoneById("Your Local Time Zone ID");\n    DateTime localTimestamp = TimeZoneInfo.ConvertTimeFromUtc(utcTimestamp, localTimeZone);\n\n    Console.WriteLine(localTimestamp);\n}\n\nreader.Close();\nconnection.Close();