using System;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic static byte[] GenerateSalt(int length)\n{\n    using (var rng = RandomNumberGenerator.Create())\n    {\n        var salt = new byte[length];\n        rng.GetBytes(salt);\n        return salt;\n    }\n}\n\npublic static byte[] HashPassword(byte[] salt, string password)\n{\n    using (var pbkdf2 = PBKDF2.Create())\n    {\n        pbkdf2.HashAlgorithm = HashAlgorithmName.SHA256;\n        pbkdf2.IterationCount = 10000;\n        return pbkdf2.GetBytes(salt, Encoding.UTF8.GetBytes(password), 16);\n    }\n}\n\n// Example usage:\nvar salt = GenerateSalt(16);\nvar hashedPassword = HashPassword(salt, "mysecretpassword");\n\n// Store salt and hashedPassword in the database