using System;\nusing System.IO;\nusing System.Runtime.Caching;\n\npublic class BinaryFileCache\n{\n    private ObjectCache cache = MemoryCache.Default;\n\n    public void CacheFile(string filePath)\n    {\n        string cacheKey = Path.GetFileName(filePath);\n        byte[] fileBytes = File.ReadAllBytes(filePath);\n        CacheItemPolicy policy = new CacheItemPolicy();\n        policy.AbsoluteExpiration = ObjectCache.InfiniteAbsoluteExpiration;\n        cache.Add(cacheKey, fileBytes, policy);\n    }\n\n    public byte[] GetCachedFile(string cacheKey)\n    {\n        return cache[cacheKey] as byte[];\n    }\n\n    public void SaveCachedFile(string cacheKey, string savePath)\n    {\n        byte[] cachedBytes = GetCachedFile(cacheKey);\n        if (cachedBytes != null)\n        {\n            File.WriteAllBytes(savePath, cachedBytes);\n        }\n    }\n\n    public void RemoveCachedFile(string cacheKey)\n    {\n        cache.Remove(cacheKey);\n    }\n}