using Moq;\n\npublic class MapinfoMock : Mock<Mapinfo>\n{\n    public MapinfoMock()\n    {\n        Setup(m => m.Do(It.IsAny<string>())).Verifiable();\n        Setup(m => m.Eval(It.IsAny<string>())).Returns("");\n    }\n\n    public void VerifyDoCalledWith(string expectedCmd)\n    {\n        Verify(m => m.Do(expectedCmd), Times.Once);\n    }\n\n    public void VerifyEvalCalledWith(string expectedCmd)\n    {\n        Verify(m => m.Eval(expectedCmd), Times.Once);\n    }\n}