public class MyClass\n{\n    [Conditional("Test")]\n    public void TestableMethod()\n    {\n        Console.WriteLine("This is test-specific code.");\n    }\n\n    public void MyMethod()\n    {\n        // Non-test code here\n    }\n}\n\n[TestClass]\npublic class MyTests\n{\n    [TestMethod]\n    public void Test_MyClass_TestableMethod()\n    {\n        var myClass = new MyClass();\n        myClass.TestableMethod(); // This will only run when the "Test" symbol is defined\n    }\n}