protected void Application_AcquireRequestState(object sender, EventArgs e)\n{\n    // Set session timeout to 3 minutes\n    Session.Timeout = 3;\n}\n\n// Later in the request...\nprotected void ExtendSessionButton_Click(object sender, EventArgs e)\n{\n    // Extend session timeout to 30 minutes\n    Session.Timeout = 30;\n    // This new timeout value will not take effect until the next request\n}