using System;\nusing System.Security.Principal;\nusing System.Diagnostics.Eventing.Reader;\n\nclass AuditEventRetriever\n{\n    public static void Main()\n    {\n        string logName = "Security";\n        string queryString = "EventID=4663 or EventID=4660 or EventID=4659 or EventID=4662";\n\n        EventLogQuery query = new EventLogQuery(logName, PathType.LogName, queryString);\n        EventLogReader reader = new EventLogReader(query);\n\n        foreach (EventRecord record in reader)\n        {\n            Console.WriteLine(record.FormatDescription());\n        }\n    }\n}