using System.Web.Routing;\n\npublic class Global : System.Web.HttpApplication\n{\n    protected void Application_Start(object sender, EventArgs e)\n    {\n        RouteTable.Routes.Add(new Route("{page}/", new PageRouteHandler()));\n    }\n}\n\npublic class PageRouteHandler : IRouteHandler\n{\n    public IHttpHandler GetHttpHandler(RequestContext requestContext)\n    {\n        string page = requestContext.RouteData.Values["page"].ToString();\n        return new PageHandler(page + ".aspx");\n    }\n}\n\npublic class PageHandler : IHttpHandler\n{\n    private string _page;\n\n    public PageHandler(string page)\n    {\n        _page = page;\n    }\n\n    public void ProcessRequest(HttpContext context)\n    {\n        context.Server.Transfer(_page);\n    }\n\n    public bool IsReusable\n    {\n        get { return false; }\n    }\n}