public static IEnumerable<string> GetWords(int length)\n{\n    const string chars = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";\n    var word = new char[length];\n\n    for (int i = 0; i < length; i++)\n    {\n        word[i] = chars[0];\n    }\n\n    while (true)\n    {\n        yield return new string(word);\n\n        int pos = length - 1;\n        while (pos >= 0 && word[pos] == chars[chars.Length - 1])\n        {\n            word[pos] = chars[0];\n            pos--;\n        }\n\n        if (pos < 0) break;\n\n        word[pos] = chars[chars.IndexOf(word[pos]) + 1];\n    }\n}