using System;\nusing System.Web;\n\npublic class ScriptInjectorModule : IHttpModule\n{\n  public void Dispose() { }\n\n  public void Init(HttpApplication context)\n  {\n    context.EndRequest += (sender, args) =>\n    {\n      var response = ((HttpApplication)sender).Response;\n      if (response.ContentType == "text/html")\n      {\n        response.Write("<script src='your-script.js'></script>");\n      }\n    };\n  }\n}