using System;\nusing System.Net.Http;\nusing System.Text;\n\nnamespace NetsuiteApiExample\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            var url = "https://webservices.netsuite.com/services/NetSuitePort_2019_1";\n            var username = "your_username";\n            var password = "your_password";\n            var accountId = "your_account_id";\n\n            var client = new HttpClient();\n            client.DefaultRequestHeaders.Authorization = new AuthenticationHeaderValue("NLAuth", $"nlauth_account={accountId}, nlauth_email={username}, nlauth_signature={password}");\n\n            var request = new HttpRequestMessage(HttpMethod.Get, $"{url}/getRecord")\n            {\n                Content = new StringContent("{\"recordType\":\"customer\",\"internalId\":\"123\"}", Encoding.UTF8, "application/json")\n            };\n\n            var response = client.SendAsync(request).Result;\n\n            Console.WriteLine(response.StatusCode);\n        }\n    }\n}