using System.IO.Pipes;\nusing System.Text;\n\n// Service side\nNamedPipeServerStream pipeServer = new NamedPipeServerStream("MyPipe", PipeDirection.Out);\npipeServer.WaitForConnection();\nusing (StreamWriter writer = new StreamWriter(pipeServer))\n{\n    writer.WriteLine("Status text");\n}\n\n// App side\nNamedPipeClientStream pipeClient = new NamedPipeClientStream(".", "MyPipe", PipeDirection.In);\npipeClient.Connect();\nusing (StreamReader reader = new StreamReader(pipeClient))\n{\n    string statusText = reader.ReadLine();\n    Console.WriteLine(statusText);\n}