using Microsoft.SharePoint.Client;\n\n// Set up the site and list context\nvar siteUrl = "https://your-sharepoint-site.sharepoint.com";\nvar listTitle = "Your List Title";\nvar ctx = new ClientContext(siteUrl);\nvar list = ctx.Web.Lists.GetByTitle(listTitle);\n\n// Get the alert subscriptions\nvar subscriptions = list.GetAlerts();\n\n// Execute the request\nctx.ExecuteQuery();\n\n// Loop through the subscriptions and extract the user information\nforeach (var subscription in subscriptions)\n{\n    Console.WriteLine($"User: {subscription.User.Name} - Alert Title: {subscription.Title}");\n}