using System.Drawing;\nusing System.Windows.Forms;\n\npublic class MulticolorLabel : RichTextBox\n{\n    public MulticolorLabel()\n    {\n        this.ReadOnly = true;\n        this.ScrollBars = RichTextBoxScrollBars.None;\n        this.BorderStyle = BorderStyle.None;\n    }\n\n    public void SetText(string text, Color[] colors)\n    {\n        this.Text = text;\n\n        int index = 0;\n        for (int i = 0; i < colors.Length; i++)\n        {\n            int length = text.IndexOf(',', index);\n            if (length == -1) length = text.Length - index;\n\n            this.Select(index, length);\n            this.SelectionColor = colors[i];\n\n            index += length + 1;\n        }\n    }\n}