using System;\nusing System.Collections.Concurrent;\nusing System.Threading;\nusing System.Threading.Tasks;\n\npublic class Program\n{\n    public static void Main(string[] args)\n    {\n        var strings = new[] { "string1", "string2", "string3", "string4", "string5" }; // Replace with your database retrieval code\n        var concurrentQueue = new ConcurrentQueue<string>(strings);\n        var semaphore = new SemaphoreSlim(3);\n\n        Task[] tasks = new Task[3];\n\n        for (int i = 0; i < 3; i++)\n        {\n            tasks[i] = Task.Run(async () =>\n            {\n                while (concurrentQueue.TryDequeue(out var item))\n                {\n                    await semaphore.WaitAsync();\n                    try\n                    {\n                        // Run PowerShell command here\n                        Console.WriteLine($"Processing {item}");\n                        await Task.Delay(1000); // Simulate intensive work\n                    }\n                    finally\n                    {\n                        semaphore.Release();\n                    }\n                }\n            });\n        }\n\n        Task.WaitAll(tasks);\n    }\n}