public partial class DetailsControl : UserControl\n{\n    private bool isValidated = false;\n\n    protected override void OnLostFocus(EventArgs e)\n    {\n        if (!isValidated)\n        {\n            this.Focus();\n        }\n        else\n        {\n            base.OnLostFocus(e);\n        }\n    }\n\n    private void DetailsControl_Validating(object sender, CancelEventArgs e)\n    {\n        // your validation logic here\n        if (!IsValid())\n        {\n            e.Cancel = true;\n        }\n    }\n\n    private void DetailsControl_Validated(object sender, EventArgs e)\n    {\n        // your save logic here\n        try\n        {\n            SaveData();\n            isValidated = true;\n        }\n        catch (Exception ex)\n        {\n            isValidated = false;\n            // handle the exception\n        }\n    }\n}